module.exports = class TransXpedia {
  constructor(option) {
    this.clientData = option || {}; //incoming client data
    /******************************************/
    this.requestData = {}; //to gateway data
    /******************************************/
    this.responseData = {}; //from gateway data
    this.responseData.gatewayResponse = null; // gateway response full data
    this.responseData.gatewayResponseCode = null; //gateway response code
    this.responseData.gatewayResponseMsg = null; //gateway response message
    this.responseData.gatewayConnTime = null; //metadata
    this.responseData.gatewayRespBackTime = null; //metadata
    this.responseData.gatewaySendTime = null; //metadata
    /*************MMI RESPONSE*****************/
    this.responseData.gatewayRefId = null;
    this.responseData.accessToken = null;
    this.responseData.address = null;
    /*#######################################*/
    /********OTP GENERATION RESPONSE*********/
    this.responseData.communicationId = null;
    this.responseData.uidType = null;
    this.responseData.uidaiAuthCode = null;
    this.responseData.txn = null;
    /*#######################################*/
    /*** ACCOUNT AND CIF CREATION RESPONSE ***/
    /********* SIGN UPLOAD RESPONSE **********/
    this.responseData.accountNo = null;
    this.responseData.customerId = null;
    this.responseData.bankreferenceNo = null;
    this.responseData.cbsstatusMsg = null;
    /*#######################################*/
    /*****************************************/
    this.errorData = {}; // module common error response
    this.errorData.serverError = {};
    this.errorData.serverError.errorCode = null; // module error code
    this.errorData.serverError.errString = null; // module error message
    this.errorData.systemErr = null; // detailed error for system exceptions if any
    this.errorData.errDescription = null; // error description for debugging
    /******************************************/
  }
};
