const Sendto = require('./eventclass');

const send = new Sendto();



send.on('sent',(msg)=>{
    console.log("sent",msg);
})

send.on('success',(msg)=>{
    console.log("success",msg);
})

send.on('failure',(msg)=>{
    console.log("failure",msg);
})
send.sendto()
.then((val)=>{console.log("val",val)})
.catch((e)=>{console.log(e)})
