const txnModule     	= require('../../TXNModule/txnModule');
const w_communication	= require('./utils.js');
const errModule     	= require('../../../Utils/error');
/* ********************************************************************************* */ 


/* ********************************************************************************* */ 
module.exports = async function (transXpedia) {
	try{
		await txnModule.cbi.ACCTandCIFCreation.frameMessage.call(this,transXpedia);

		await w_communication.call(this,transXpedia);

		return await txnModule.cbi.ACCTandCIFCreation.extractMessage.call(this,transXpedia);

	} catch(e) {
			throw transXpedia;
	}
}
